con:config.script:
// Used to create the Request XML and add values
import com.eviware.soapui.support.XmlHolder
// Used to Run the Request
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
// Get access to opencsv
import com.opencsv.CSVWriter

def getFilePath(fileName) {
	// Dynamically get Project Path, Folders Path (Resources) and 
	// Get Full classified path name for each txt file
	def projPath = new com.eviware.soapui.support.GroovyUtils(context).getProjectPath()
	def resourcesPath = projPath + '\\' + context.expand('${Properties#resourcesLoc}') + '\\'
	def filePath = resourcesPath + context.expand(fileName)
	return filePath
}

def loadFile(fileName) {
	return new File(getFilePath(fileName))
}

def getSOAPStep(reqName) {
	return testRunner.testCase.testSteps[reqName]
}

def runSOAPStep(reqName, nodePath, nodeValue) {
	def soapName = getSOAPStep(reqName)
	def soapReq = soapName.getPropertyValue("Request")
	def soapXml = new XmlHolder(soapReq)
	soapXml.setNodeValue(nodePath, nodeValue)
	def xmlReq = soapXml.getXml()
	soapName.setPropertyValue("Request", xmlReq)
	def wsdlCon = new WsdlTestRunContext(soapName)
	soapName.run(testRunner, wsdlCon)
}

def getSingleResVal(reqName, nodePath) {
	def soapName = getSOAPStep(reqName)
	def soapRes = soapName.getPropertyValue("Response")
	def xmlRes = new XmlHolder(soapRes)
	return xmlRes.getNodeValue(nodePath)
}

File countryFile = loadFile('${Properties#countryList}')
def noIsoCode = loadFile('${Properties#noCodeCountryList}') as String[]

CSVWriter writer = new CSVWriter(new FileWriter(getFilePath('${Properties#fullCountryInfo}')))
String[] headers = ['ISO Code', 'Name', 'Capital City', 'Phone Code', 'Continent Code', 'Currency ISO Code', 'Country Flag', 'Language ISO Code', 'Language Name']
writer.writeNext(headers)

countryFile.eachLine { name ->
	if (name in noIsoCode ) {
		return
	}
	// SOAP Request - Get ISO Code Block
	runSOAPStep("SOAP Request - Get ISO Code", "//web:CountryISOCode/web:sCountryName", name)
	def isoRes = getSingleResVal("SOAP Request - Get ISO Code", "//m:CountryISOCodeResult")

	// SOAP Request - Get Capital Block
	runSOAPStep("SOAP Request - Get Capital", "//web:CapitalCity/web:sCountryISOCode", isoRes)
	def capitalRes = getSingleResVal("SOAP Request - Get Capital", "//m:CapitalCityResult")

	// SOAP Request - Full Info Block
	runSOAPStep("SOAP Request - Full Info", "//web:FullCountryInfo/web:sCountryISOCode", isoRes)
	def soapName = getSOAPStep("SOAP Request - Full Info")
	def soapRes = soapName.getPropertyValue("Response")
	def xmlRes = new XmlHolder(soapRes)
	def resList = []
	if (xmlRes.getNodeValue("//m:sCapitalCity") == capitalRes) {
		resList.add(xmlRes.getNodeValue("//m:sISOCode"))
		resList.add(xmlRes.getNodeValue("//m:sName"))
		resList.add(xmlRes.getNodeValue("//m:sCapitalCity"))
		resList.add(xmlRes.getNodeValue("//m:sPhoneCode"))
		resList.add(xmlRes.getNodeValue("//m:sContinentCode"))
		resList.add(xmlRes.getNodeValue("//m:sCurrencyISOCode"))
		resList.add(xmlRes.getNodeValue("//m:sCountryFlag"))
		resList.add(xmlRes.getNodeValue("//m:Languages/m:tLanguage/m:sISOCode"))
		resList.add(xmlRes.getNodeValue("//m:Languages/m:tLanguage/m:sName"))
	}
	try {
		writer.writeNext(resList as String[])
	} catch (Exception e) {
		e.printStackTrace()
	}
}

writer.close()
